sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isPastDate : function(strDate){

		//create GlideDateTime objects for current and testdate
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//if testdate is before right now return true else return false
		if (testdate.before(rightnow)){
			return true;
		}
		else{
			return false;
		}
	},

	istoday : function(strDate){
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		var today = rightnow.getLocalDate();
		var checkDate = testdate.getLocalDate();

		if(checkDate.compareTo(today) == 0){
		return true;
		}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
